var bcd2number = function (num) {
    let loByte = (num & 0x00ff);
    let hiByte = (num >> 8) & 0x00ff;
    let n = 0;
    n += (loByte & 0x0F) * 1;
    n += ((loByte >> 4) & 0x0F) * 10;
    n += (hiByte & 0x0F) * 100;
    n += ((hiByte >> 4) & 0x0F) * 1000;
    return n;
}

//all of them at once to BCD
msg.bcd = msg.payload.map(bcd2number);

